import { useEffect, useState } from 'react';
import api from '../services/api';

function AdminDashboard() {
  const [analytics, setAnalytics] = useState({ totalUsers: 0, totalProducts: 0, totalOrders: 0 });

  useEffect(() => {
    api.get('/admin/analytics')
      .then((res) => setAnalytics(res.data))
      .catch((err) => alert('Failed to fetch analytics: ' + (err.response?.data?.msg || 'Unknown error')));
  }, []);

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-bold mb-4">Super Admin Dashboard</h1>
      <div className="grid grid-cols-3 gap-4">
        <div className="p-4 bg-white rounded shadow">
          <h2 className="text-xl font-semibold">Total Users</h2>
          <p className="text-2xl">{analytics.totalUsers}</p>
        </div>
        <div className="p-4 bg-white rounded shadow">
          <h2 className="text-xl font-semibold">Total Products</h2>
          <p className="text-2xl">{analytics.totalProducts}</p>
        </div>
        <div className="p-4 bg-white rounded shadow">
          <h2 className="text-xl font-semibold">Total Orders</h2>
          <p className="text-2xl">{analytics.totalOrders}</p>
        </div>
      </div>
    </div>
  );
}

export default AdminDashboard;
