import { useEffect, useState } from 'react';
import api from '../services/api';

function Home() {
  const [products, setProducts] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    console.log('Home.js - Fetching products...'); // Debug log
    const fetchProducts = async () => {
      try {
        const res = await api.get('/products');
        console.log('Home.js - Products fetched:', res.data); // Debug log
        setProducts(res.data);
      } catch (err) {
        console.error('Home.js - Error fetching products:', err); // Debug log
        setError('Failed to fetch products: ' + (err.response?.data?.msg || 'Unknown error'));
      }
    };
    fetchProducts();
  }, []);

  console.log('Home.js - Rendering, products:', products, 'error:', error); // Debug log

  if (error) {
    return (
      <div className="container mx-auto p-4">
        <h1 className="text-3xl font-bold mb-4">Home</h1>
        <p className="text-red-500">{error}</p>
      </div>
    );
  }

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-bold mb-4">Welcome to Bamboo Marketplace</h1>
      {products.length === 0 ? (
        <p>No products available.</p>
      ) : (
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4">
          {products.map((product) => (
            <div key={product._id} className="bg-white p-4 rounded shadow">
              <img
                src={product.image}
                alt={product.name}
                className="w-full h-48 object-cover mb-4"
                onError={(e) => (e.target.src = 'https://placehold.co/150x150')}
              />
              <h3 className="text-lg font-semibold">{product.name}</h3>
              <p>{product.description}</p>
              <p className="text-green-600 font-bold">${product.price}</p>
              <p>Stock: {product.stock}</p>
              <button
                onClick={() => handleAddToCart(product._id)}
                className="mt-2 bg-blue-500 text-white p-2 rounded hover:bg-blue-600"
                disabled={product.stock === 0}
              >
                {product.stock === 0 ? 'Out of Stock' : 'Add to Cart'}
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

const handleAddToCart = async (productId) => {
  const userId = sessionStorage.getItem('userId');
  if (!userId) {
    alert('Please log in to add items to your cart.');
    window.location.href = '/login';
    return;
  }

  try {
    const userRes = await api.get('/auth/me');
    const user = userRes.data;
    const cart = user.cart || [];
    const existingItem = cart.find((item) => item.productId === productId);

    if (existingItem) {
      existingItem.quantity += 1;
    } else {
      cart.push({ productId, quantity: 1 });
    }

    await api.post('/auth/me', { cart });
    alert('Product added to cart!');
  } catch (err) {
    alert('Failed to add to cart: ' + (err.response?.data?.msg || 'Unknown error'));
  }
};

export default Home;
