import { useEffect, useState } from 'react';
import api from '../services/api';

function Cart() {
  const [cart, setCart] = useState([]);

  useEffect(() => {
    api.get('/auth/me').then((res) => setCart(res.data.cart));
  }, []);

  const handleCheckout = async () => {
    await api.post('/orders/checkout', { products: cart });
    alert('Order placed successfully!');
    setCart([]);
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-bold mb-4">Cart</h1>
      {cart.map((item) => (
        <div key={item.productId} className="p-4 bg-white rounded shadow mb-4">
          <p>Product ID: {item.productId}</p>
          <p>Quantity: {item.quantity}</p>
        </div>
      ))}
      <button onClick={handleCheckout} className="mt-4 bg-green-500 text-white p-2 rounded">Checkout</button>
    </div>
  );
}

export default Cart;
