import { useEffect, useState } from 'react';
import api from '../services/api';
import { Range } from 'react-range';

function Home() {
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [sortOption, setSortOption] = useState('default');
  const [priceRange, setPriceRange] = useState([0, 1000]);
  const [searchQuery, setSearchQuery] = useState('');

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const res = await api.get('/products');
        setProducts(res.data);
        setFilteredProducts(res.data);

        const uniqueCategories = ['All', ...new Set(res.data.map((product) => product.category || 'Uncategorized'))];
        setCategories(uniqueCategories);
      } catch (err) {
        alert('Failed to fetch products: ' + (err.response?.data?.msg || 'Unknown error'));
      }
    };
    fetchProducts();
  }, []);

  useEffect(() => {
    let filtered = [...products];

    if (selectedCategory !== 'All') {
      filtered = filtered.filter((product) => product.category === selectedCategory);
    }

    filtered = filtered.filter((product) => product.price >= priceRange[0] && product.price <= priceRange[1]);

    if (searchQuery) {
      filtered = filtered.filter((product) =>
        product.name.toLowerCase().includes(searchQuery.toLowerCase())
      );
    }

    if (sortOption === 'lowToHigh') {
      filtered.sort((a, b) => a.price - b.price);
    } else if (sortOption === 'highToLow') {
      filtered.sort((a, b) => b.price - a.price);
    }

    setFilteredProducts(filtered);
  }, [selectedCategory, sortOption, priceRange, searchQuery, products]);

  const handleAddToCart = async (productId) => {
    try {
      const userId = sessionStorage.getItem('userId');
      if (!userId) {
        alert('Please log in to add items to your cart.');
        return;
      }

      const res = await api.get('/auth/me');
      const user = res.data;
      const cart = user.cart || [];
      const existingItem = cart.find((item) => item.productId === productId);

      if (existingItem) {
        existingItem.quantity += 1;
      } else {
        cart.push({ productId, quantity: 1 });
      }

      await api.post('/auth/me', { cart });
      alert('Product added to cart!');
    } catch (err) {
      alert('Failed to add to cart: ' + (err.response?.data?.msg || 'Unknown error'));
    }
  };

  return (
    <div className="container mx-auto p-4">
      {/* Search Bar */}
      <div className="mb-4">
        <input
          type="text"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          placeholder="Search for products..."
          className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500"
        />
      </div>

      {/* Category Tabs */}
      <div className="flex space-x-4 mb-4 overflow-x-auto">
        {categories.map((category) => (
          <button
            key={category}
            onClick={() => setSelectedCategory(category)}
            className={`px-4 py-2 rounded-lg font-medium ${
              selectedCategory === category ? 'bg-green-600 text-white' : 'bg-gray-200 text-gray-700'
            }`}
          >
            {category}
          </button>
        ))}
      </div>

      {/* Filters and Sorting */}
      <div className="flex justify-between items-center mb-4">
        <div className="flex items-center space-x-4">
          <div className="w-64">
            <label className="block text-gray-700 mb-2">Price Range</label>
            <Range
              values={priceRange}
              step={10}
              min={0}
              max={1000}
              onChange={(values) => setPriceRange(values)}
              renderTrack={({ props, children }) => (
                <div
                  {...props}
                  className="h-2 bg-gray-200 rounded"
                  style={{ ...props.style }}
                >
                  <div
                    className="h-2 bg-green-600 rounded"
                    style={{
                      width: `${((priceRange[1] - priceRange[0]) / 1000) * 100}%`,
                      marginLeft: `${(priceRange[0] / 1000) * 100}%`,
                    }}
                  />
                  {children}
                </div>
              )}
              renderThumb={({ props }) => (
                <div
                  {...props}
                  className="h-6 w-6 bg-green-600 rounded-full flex items-center justify-center focus:outline-none"
                  style={{ ...props.style }}
                />
              )}
            />
            <div className="flex justify-between mt-2">
              <span className="text-gray-600">${priceRange[0]}</span>
              <span className="text-gray-600">${priceRange[1]}</span>
            </div>
          </div>
        </div>
        <div>
          <label className="block text-gray-700 mb-2">Sort By</label>
          <select
            value={sortOption}
            onChange={(e) => setSortOption(e.target.value)}
            className="p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-green-500"
          >
            <option value="default">Default</option>
            <option value="lowToHigh">Price: Low to High</option>
            <option value="highToLow">Price: High to Low</option>
          </select>
        </div>
      </div>

      {/* Product Grid */}
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
        {filteredProducts.length === 0 ? (
          <p>No products found.</p>
        ) : (
          filteredProducts.map((product) => (
            <div key={product._id} className="bg-white p-4 rounded-lg shadow-md">
              <img
                src={product.image}
                alt={product.name}
                className="w-full h-48 object-cover rounded mb-4"
                onError={(e) => (e.target.src = 'https://placehold.co/150x150')}
              />
              <h3 className="text-lg font-semibold mb-2">{product.name}</h3>
              <p className="text-gray-600 mb-2">{product.description}</p>
              <p className="text-green-700 font-medium mb-2">${product.price}</p>
              <button
                onClick={() => handleAddToCart(product._id)}
                className="w-full bg-green-600 text-white p-2 rounded-lg hover:bg-green-700"
              >
                Add to Cart
              </button>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default Home;
