import { BrowserRouter as Router, Route, Routes, Link, useNavigate } from 'react-router-dom';
import { useContext, useState, useEffect } from 'react';
import { AuthContext } from './context/AuthContext';
import { AuthProvider } from './context/AuthContext';
import Home from './pages/Home';
import Cart from './pages/Cart';
import Profile from './pages/Profile';
import Success from './pages/Success';
import DealerDashboard from './pages/DealerDashboard';
import AdminDashboard from './pages/AdminDashboard';
import OrderHistory from './pages/OrderHistory';
import OrderDetails from './pages/OrderDetails';
import ErrorBoundary from './components/ErrorBoundary';
import ProtectedRoute from './components/ProtectedRoute';
import AuthModal from './components/AuthModal';
import api from './services/api';

function App() {
  const navigate = useNavigate();
  const { isLoggedIn, userRole, logout } = useContext(AuthContext);
  const [isAuthModalOpen, setIsAuthModalOpen] = useState(false);
  const [authMode, setAuthMode] = useState('login');
  const [userName, setUserName] = useState('');
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  useEffect(() => {
    const fetchUser = async () => {
      if (isLoggedIn) {
        try {
          const res = await api.get('/auth/me');
          setUserName(res.data.name);
        } catch (err) {
          console.error('Failed to fetch user:', err);
        }
      }
    };
    fetchUser();
  }, [isLoggedIn]);

  const handleLogout = () => {
    logout();
    setUserName('');
    alert('Logged out successfully!');
    navigate('/');
  };

  const openAuthModal = (mode) => {
    setAuthMode(mode);
    setIsAuthModalOpen(true);
  };

  return (
    <ErrorBoundary>
      <div className="min-h-screen">
        <nav className="bg-white shadow-md p-4">
          <div className="container mx-auto flex justify-between items-center">
            <Link to="/" className="text-2xl font-bold text-green-700">Bamboo Marketplace</Link>
            <div className="flex items-center space-x-6">
              {isLoggedIn ? (
                <>
                  {userRole === 'citizen' && (
                    <>
                      <Link to="/cart" className="text-gray-700 hover:text-green-700 font-medium">Cart</Link>
                      <Link to="/orders" className="text-gray-700 hover:text-green-700 font-medium">Orders</Link>
                    </>
                  )}
                  <div className="relative">
                    <button
                      onClick={() => setIsDropdownOpen(!isDropdownOpen)}
                      className="text-gray-700 hover:text-green-700 font-medium flex items-center"
                    >
                      {userName || 'My Account'} â–¼
                    </button>
                    {isDropdownOpen && (
                      <div className="absolute right-0 mt-2 w-48 bg-white rounded-lg shadow-lg z-10">
                        <Link
                          to="/profile"
                          className="block px-4 py-2 text-gray-700 hover:bg-green-100 hover:text-green-700"
                          onClick={() => setIsDropdownOpen(false)}
                        >
                          Profile
                        </Link>
                        {userRole === 'dealer' && (
                          <Link
                            to="/dealer-dashboard"
                            className="block px-4 py-2 text-gray-700 hover:bg-green-100 hover:text-green-700"
                            onClick={() => setIsDropdownOpen(false)}
                          >
                            Dealer Dashboard
                          </Link>
                        )}
                        {userRole === 'superadmin' && (
                          <Link
                            to="/admin-dashboard"
                            className="block px-4 py-2 text-gray-700 hover:bg-green-100 hover:text-green-700"
                            onClick={() => setIsDropdownOpen(false)}
                          >
                            Admin Dashboard
                          </Link>
                        )}
                        <button
                          onClick={() => {
                            handleLogout();
                            setIsDropdownOpen(false);
                          }}
                          className="block w-full text-left px-4 py-2 text-gray-700 hover:bg-green-100 hover:text-green-700"
                        >
                          Logout
                        </button>
                      </div>
                    )}
                  </div>
                </>
              ) : (
                <>
                  <button
                    onClick={() => openAuthModal('login')}
                    className="text-gray-700 hover:text-green-700 font-medium"
                  >
                    Login
                  </button>
                  <button
                    onClick={() => openAuthModal('register')}
                    className="text-gray-700 hover:text-green-700 font-medium"
                  >
                    Sign Up
                  </button>
                </>
              )}
            </div>
          </div>
        </nav>
        <AuthModal
          isOpen={isAuthModalOpen}
          onClose={() => setIsAuthModalOpen(false)}
          initialMode={authMode}
        />
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/cart" element={<ProtectedRoute allowedRoles={['citizen']}><Cart /></ProtectedRoute>} />
          <Route path="/profile" element={<ProtectedRoute allowedRoles={['citizen', 'dealer', 'superadmin']}><Profile /></ProtectedRoute>} />
          <Route path="/success" element={<Success />} />
          <Route path="/dealer-dashboard" element={<ProtectedRoute allowedRoles={['dealer']}><DealerDashboard /></ProtectedRoute>} />
          <Route path="/admin-dashboard" element={<ProtectedRoute allowedRoles={['superadmin']}><AdminDashboard /></ProtectedRoute>} />
          <Route path="/orders" element={<ProtectedRoute allowedRoles={['citizen']}><OrderHistory /></ProtectedRoute>} />
          <Route path="/order/:id" element={<ProtectedRoute allowedRoles={['citizen']}><OrderDetails /></ProtectedRoute>} />
        </Routes>
      </div>
    </ErrorBoundary>
  );
}

export default function AppWrapper() {
  return (
    <Router>
      <AuthProvider>
        <App />
      </AuthProvider>
    </Router>
  );
}
