import { useEffect, useState } from 'react';
import api from '../services/api';

function DealerDashboard() {
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({
    name: '',
    description: '',
    price: '',
    stock: '',
    image: '',
  });

  useEffect(() => {
    api.get('/products')
      .then((res) => setProducts(res.data || []))
      .catch((err) => console.error('Error fetching products:', err));
  }, []);

  const handleAddProduct = async (e) => {
    e.preventDefault();
    try {
      const res = await api.post('/products', newProduct);
      setProducts([...products, res.data]);
      alert('Product added successfully!');
      setNewProduct({ name: '', description: '', price: '', stock: '', image: '' });
    } catch (err) {
      alert('Failed to add product: ' + (err.response?.data?.msg || 'Unknown error'));
    }
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-bold mb-4">Dealer Dashboard</h1>
      <div className="mb-8">
        <h2 className="text-2xl font-semibold mb-4">Add New Product</h2>
        <form onSubmit={handleAddProduct} className="max-w-md">
          <input
            type="text"
            value={newProduct.name}
            onChange={(e) => setNewProduct({ ...newProduct, name: e.target.value })}
            placeholder="Product Name"
            className="w-full p-2 mb-4 border rounded"
            required
          />
          <input
            type="text"
            value={newProduct.description}
            onChange={(e) => setNewProduct({ ...newProduct, description: e.target.value })}
            placeholder="Description"
            className="w-full p-2 mb-4 border rounded"
            required
          />
          <input
            type="number"
            value={newProduct.price}
            onChange={(e) => setNewProduct({ ...newProduct, price: e.target.value })}
            placeholder="Price"
            className="w-full p-2 mb-4 border rounded"
            required
          />
          <input
            type="number"
            value={newProduct.stock}
            onChange={(e) => setNewProduct({ ...newProduct, stock: e.target.value })}
            placeholder="Stock"
            className="w-full p-2 mb-4 border rounded"
            required
          />
          <input
            type="text"
            value={newProduct.image}
            onChange={(e) => setNewProduct({ ...newProduct, image: e.target.value })}
            placeholder="Image URL"
            className="w-full p-2 mb-4 border rounded"
            required
          />
          <button type="submit" className="bg-green-500 text-white p-2 rounded">Add Product</button>
        </form>
      </div>
      <h2 className="text-2xl font-semibold mb-4">Your Products</h2>
      <div className="grid grid-cols-3 gap-4">
        {products.map((product) => (
          <div key={product._id} className="p-4 bg-white rounded shadow">
            <img src={product.image} alt={product.name} className="w-full h-48 object-cover" />
            <h3 className="text-xl">{product.name}</h3>
            <p>{product.description}</p>
            <p>${product.price}</p>
            <p>Stock: {product.stock}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default DealerDashboard;
