import { useEffect, useState } from 'react';
import api from '../services/api';
import { useNavigate } from 'react-router-dom';

function Cart() {
  const [cart, setCart] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    api.get('/auth/me')
      .then((res) => {
        // Ensure cart exists in the response, default to empty array if not
        setCart(res.data.cart || []);
      })
      .catch((err) => {
        console.error('Error fetching user data:', err);
        setCart([]); // Fallback to empty array on error
      });
  }, []);

  const handleCheckout = async () => {
    if (cart.length === 0) {
      alert('Your cart is empty!');
      return;
    }
    try {
      await api.post('/orders/checkout', { products: cart });
      alert('Order placed successfully!');
      setCart([]);
      navigate('/success'); // Redirect to success page
    } catch (err) {
      alert('Checkout failed: ' + err.response?.data?.msg || 'Unknown error');
    }
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-bold mb-4">Cart</h1>
      {cart.length === 0 ? (
        <p>Your cart is empty.</p>
      ) : (
        <>
          {cart.map((item) => (
            <div key={item.productId} className="p-4 bg-white rounded shadow mb-4">
              <p>Product ID: {item.productId}</p>
              <p>Quantity: {item.quantity}</p>
            </div>
          ))}
          <button
            onClick={handleCheckout}
            className="mt-4 bg-green-500 text-white p-2 rounded hover:bg-green-600"
          >
            Checkout
          </button>
        </>
      )}
    </div>
  );
}

export default Cart;
